import React, {
  useContext
} from "react";
import Context from "../context";

const styles = {
  li: {
    display: "flex",
    justifyContent: "space-between",
    alignItems: "center",
    padding: ".5rem 1rem",
    border: "1px solid black",
    borderRadius: "4px",
    marginBottom: ".5rem",
    boxShadow: "4px 4px peru",
  },
  input: {
    marginRight: "1rem",
  },
};

function TodoItem({
  todo,
  index,
  onChange
}) {
  const {
    removeTodo
  } = useContext(Context);
  const classes = [];
  if (todo.completed) {
    classes.push("done");
  }
  return ( <
    li style = {
      styles.li
    } >
    <
    span className = {
      classes.join(" ")
    } >
    <
    input style = {
      styles.input
    }
    type = "checkbox"
    checked = {
      todo.completed
    }
    onChange = {
      () => onChange(todo.id)
    }
    /> <
    strong > {
      index + 1
    } < /strong> &
    nbsp; {
      todo.title
    } <
    /span> <
    button className = "rm"
    onClick = {
      () => removeTodo(todo.id)
    } >
    &
    times; <
    /button> <
    button onClick = {
      () => removeTodo(todo.id)
    } > !!!! < /button> <
    /li>
  );
}

export default TodoItem;
